# fix_config_booleans.py

from pathlib import Path

# Path to your autogenerated config
cfg = Path('config/regions_config.py')

# Read it
text = cfg.read_text(encoding='utf-8')

# Replace JSON booleans with Python booleans
fixed = (
    text
    .replace(': false', ': False')
    .replace(': true',  ': True')
)

# Write back
cfg.write_text(fixed, encoding='utf-8')
print("âœ… Updated booleans in regions_config.py")
